DROP EVENT IF EXISTS `order_update_status_event`;
delimiter ;;
CREATE EVENT `order_update_status_event`
ON SCHEDULE
EVERY '1' DAY STARTS '2023-05-21 17:48:20'
DO BEGIN
        UPDATE Orders
        SET `status` = CASE
            WHEN `status` = 'Đã xác nhận' THEN 'Đang đóng gói'
            WHEN `status` = 'Đang đóng gói' THEN 'Đang vận chuyển'
            WHEN `status` = 'Đang vận chuyển' THEN
                CASE FLOOR(RAND() * 3)
                    WHEN 0 THEN 'Vận chuyển thành công'
                    WHEN 1 THEN 'Vận chuyển không thành công'
                    WHEN 2 THEN 'Đã hủy'
                END
            ELSE `status`
        END
        WHERE orderDate <= DATE_SUB(NOW(), INTERVAL 1 DAY);
    END
;;
